import React from 'react';
import Backend from '../api/Backend';

class ChapterSelector extends React.Component
{
    constructor(props){
        super(props);
        this.state = {
            list: []
        };
    }
    
    async componentDidMount(){
        // load
        const list = await Backend.Series.chapters(this.props.seriesId);
        this.setState({list: list});
    }
   
    onChapterChanged(e){
       this.props.onChapterChanged(e.target.value);
    }

    render(){
        const chapters = [];
        this.state.list.forEach(chapter => {
            let elem = <option value={chapter}>{chapter.name}</option>;
            // set selected item
            if(this.props.selected === chapter.id){
                elem.selected = true;
            }
            chapters.push(elem);
        });

        return (
            <select onChange={this.onChapterChanged.bind(this)}>
                {chapters}
            </select>
        );
    }

}

export default ChapterSelector;
